    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
      // add book
     String BookName= this.jTextField1.getText();
     int    BookID  = parseInt(this.jTextField2.getText());
     int    NoOfPages=parseInt(this.jTextField3.getText());
     String category= this.jTextField4.getText();
     AddBookController a=new AddBookController();// communicates with the controller
     a.addBook(BookName,BookID,NoOfPages,category);// through this method
       jTextField1.setText("");
        jTextField2.setText("");
        jTextField3.setText("");
        jTextField4.setText("");
        jTextField6.setText("");
    }//GEN-LAST:event_jButton1ActionPerformed

