    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
      // search book button
       String category=jTextField5.getText();
       SearchBookController sbc=new SearchBookController();// object of controller
       sbc.SearchCategory(category);// method inside the controller  that the boundry communicates with the controller with
       jTextField5.setText("");
    }//GEN-LAST:event_jButton2ActionPerformed

    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
   // remove book ... same as add book 
     String BookName= this.jTextField1.getText();
     int    BookID  = parseInt(this.jTextField2.getText());
     int    NoOfPages=parseInt(this.jTextField3.getText());
     String category= this.jTextField4.getText();
     RemoveBookController r=new RemoveBookController();
     r.removeBook(BookName,BookID,NoOfPages,category);
       jTextField1.setText("");
        jTextField2.setText("");
        jTextField3.setText("");
        jTextField4.setText("");
        jTextField6.setText("");
    }//GEN-LAST:event_jButton3ActionPerformed

    private void jButton5ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton5ActionPerformed
         String BookName= this.jTextField1.getText();
        int    BookID  = parseInt(this.jTextField2.getText());
        int    NoOfPages=parseInt(this.jTextField3.getText());
        String category= this.jTextField4.getText();
        int    NoOfCopies= parseInt(this.jTextField6.getText());
        RemoveBookCopyController r=new RemoveBookCopyController();
        r.RemoveBookCopy(BookName,BookID,NoOfPages,category,NoOfCopies);
        jTextField1.setText("");
        jTextField2.setText("");
        jTextField3.setText("");
        jTextField4.setText("");
        jTextField6.setText("");
    }//GEN-LAST:event_jButton5ActionPerformed

    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
       // add book copy
        String BookName= this.jTextField1.getText();
        int    BookID  = parseInt(this.jTextField2.getText());
        int    NoOfPages=parseInt(this.jTextField3.getText());
        String category= this.jTextField4.getText();
        int    NoOfCopies= parseInt(this.jTextField6.getText());
        AddBookCopyController a=new AddBookCopyController();
        a.AddBookCopy(BookName,BookID,NoOfPages,category,NoOfCopies);// boundry talks to controller through that one
        jTextField1.setText("");
        jTextField2.setText("");
        jTextField3.setText("");
        jTextField4.setText("");
        jTextField6.setText("");

    }//GEN-LAST:event_jButton4ActionPerformed

